function toggleMobileMenu() {
  const e = document.getElementById('mobileMenu'),
    t = document.querySelector('.mobile-menu-toggle'),
    o = t.querySelector('i'),
    i = !('true' === t.getAttribute('aria-expanded'));
  (e.classList.toggle('active', i),
    t.classList.toggle('active', i),
    t.setAttribute('aria-expanded', String(i)),
    e.removeAttribute('style'),
    (o.textContent = i ? 'close' : 'menu'));
}
